        e.preventDefault();
     dispatch(updateUser());
      }

      const onChange=(e)=>{
        setselectedUser({...selectedUser,[e.target.name]:e.target.value})
       }

  return (
    <div>

    <form >
        <div class="form-group pt-5">
            <label for="formGroupExampleInput mt-1"> Admin Name</label>
            <input type="text" class="form-control mt-2" id="formGroupExampleInput" placeholder="Enter admin name" value={users.name} onChange={(e)=>setUsers({...users,name:e.target.value})}/>
        </div>
        <div class="form-group pt-5">
            <label for="formGroupExampleInput2">Admin Email</label>
            <input type="email" class="form-control mt-2" id="formGroupExampleInput2" placeholder="Enter admin email" value={users.email} onChange={(e)=>setUsers({...users,email:e.target.value})}/>
        </div>
        <div class="form-group pt-5">
            <label for="formGroupExampleInput2">Admin Address</label>
            <input type="text" class="form-control mt-2" id="formGroupExampleInput2 " placeholder="Enter admin address" value={users.address} onChange={(e)=>setUsers({...users,address:e.target.value})}/>
        </div>
       <button type="submit" className="btn btn-outline-success mt-5" onClick={handleCklick}>edit </button>

    </form>

</div>
  )
}

export default Update
