import React,{useState} from 'react'
import { useSelector,useDispatch } from 'react-redux/es/exports';
import { updateUser } from '../Action';
const Update = () => {
    const [selectedUser,setselectedUser]=useState({
        id:'',
        name:'',
        email:'',
        address:''
      });

      const dispatch= useDispatch();

      const handleCklick=(e)=>{
        e.preventDefault();

        const currentUserID=props.match.params.id;
        useEffect(()=>{
          const userId=currentUserID;
          const selectedUser=users.find(user=>user.id===userId);
          setselectedUser(selectedUser)
        },[currentUserID,users])
     dispatch(updateUser());
      }

      const onChange=(e)=>{
        setselectedUser({...selectedUser,[e.target.name]:e.target.value})
       }

  return (
    <div>

    <form >
        <div class="form-group pt-5">
            <label for="formGroupExampleInput mt-1"> Admin Name</label>
            <input type="text" class="form-control mt-2" id="formGroupExampleInput" placeholder="Enter admin name" value={selectedUser.name} onChange={onChange}/>
        </div>
        <div class="form-group pt-5">
            <label for="formGroupExampleInput2">Admin Email</label>
            <input type="email" class="form-control mt-2" id="formGroupExampleInput2" placeholder="Enter admin email" value={selectedUser.email} onChange={onChange}/>
        </div>
        <div class="form-group pt-5">
            <label for="formGroupExampleInput2">Admin Address</label>
            <input type="text" class="form-control mt-2" id="formGroupExampleInput2 " placeholder="Enter admin address" value={selectedUser.address} onChange={onChange}/>
        </div>
       <button type="submit" className="btn btn-outline-success mt-5" onClick={handleCklick}>edit </button>

    </form>

</div>
  )
}

export default Update
