             edit
             </button>
             <button className="btn btn-danger"  onClick={() => {dispatch(deleteUser(el.id)); swal({
        title: "Good job!",
        text: "user deleted success!",
        icon: "success",
        button: "ok!",
      }).then(function() {
        window.location.href="/";
    })}}> Delete</button>
             {/* <button  onClick={() => {dispatch(deleteUser(el.id))}}>X</button> */}
             {/* <button  onClick={() => {dispatch(updateUser(el.id))}}>edit</button> */}

       </div>
       </div>







       )
      })} 
       
    
    </div>
  )
}

export default Home
