import { act } from '@testing-library/react';
import React from 'react'
const initialState=[]
export const projectReducer = (state=initialState,action) => {
 switch (action.type) {
    case "FETCH_DATA":
       return  action.payload;
    case "ADD_USER"  :
        return {
            users:[action.payload,...state.users]
        }
 
    default:
       return state;
 }
}
