import axios from "axios";



export const fetchData = () => {
    return async(dispatch,getstate)=>{
        const response=await axios.get("https://62de95669c47ff309e7729bf.mockapi.io/users");
        console.log(response.data)
        dispatch({
            type:"FETCH_DATA",
            payload:response.data
        })
    }
}

