import { useSelector,useDispatch } from 'react-redux/es/exports';
import { fetchData } from '../Action';
import React, { useEffect, useState } from 'react'
import { deleteUser } from '../Action';
import axios from 'axios';

const Home = () => {
  
 const dispatch=useDispatch();
 const users=useSelector((state)=>state)
   useEffect(()=>{
     dispatch(fetchData());
   },[])

  //  const handleDelete = (id) => {
  //   dispatch(deleteUser({ id }));
  // };
  const deleteUser =(id)=>{
    fetch(`https://62de95669c47ff309e7729bf.mockapi.io/users/${id}` , {method:'DELETE'})
     const user = users.filter((ep)=> ep.id !== id)
     window.location.href = "http://localhost:3000/Movies";
  }
  return (
    <div  class="row">
           {users.length>0 && users.map(el=>{
    return(
        
        <div className="card col-md-4 mt-2">
        <div className="card-body">
        
        <h3 className="mb-2">{el.name}</h3>
             <p className="card-text">{el.email}</p>
           
             <p className="card-text"> { el.address}</p>
           
             <button className="btn btn-danger"  style={{ marginRight: "10px" }}>
            Show
             </button>
             <button className="btn btn-danger" > Delete</button>
             <button  onClick={() => deleteUser({id:el.id})}>X</button>

       </div>
       </div>







       )
      })} 
       
    
    </div>
  )
}

export default Home
