import { act } from '@testing-library/react';
import concat from 'redux-concatenate-reducers'

import React from 'react'
// const initialState=[]
export const projectReducer = (state={ user: [] },action) => {
 switch (action.type) {
    case "FETCH_DATA":
       return  action.payload;
    case "ADD_USER"  :
        return{
        user:[...state.user,action.payload]
    }
    case "UPDATE_USER":
        const updatedUser=action.payload
        const updatedUsers=state.user.map(user=>{
            if(user.id=== updatedUser.id){
                return updatedUser;
            }
            return user;
        })
        return{
            users:updatedUsers
        }  
          
 
    default:
       return state;
 }
}
