import axios from "axios";



export const fetchData = () => {
    return async(dispatch,getstate)=>{
        const response=await axios.get("https://62de95669c47ff309e7729bf.mockapi.io/users");
        console.log(response.data)
        dispatch({
            type:"FETCH_DATA",
            payload:response.data
        })
    }
}



export const addUser=(name,email,address)=>{
    return(dispatch)=>{
        axios.post('https://62de95669c47ff309e7729bf.mockapi.io/users',{name,email,address})
        .then(response=>{
            console.log(response.data);
            dispatch({
                type:'ADD_USER',
                payload:response.data
            })
            .catch(error=>{
                console.log(error)
            });
        })
    }
}

export const updateUser=(id,name,email,address)=>{
    return(dispatch)=>{
        axios.put(`https://62de95669c47ff309e7729bf.mockapi.io/users/${id}`,{id,name,email,address})
        .then(response=>{
            console.log(response.data);
            dispatch({
                type:'UPDATE_USER',
                payload:response.user
            })
            .catch(error=>{
                console.log(error)
            });
        })
    }
}


export const deleteUser = id => dispatch => {
    fetch(`https://62de95669c47ff309e7729bf.mockapi.io/users/${id}`, {
      method: "DELETE"
    })
    //   .then(response => response.json())
    //   .then(users =>
    //     dispatch({
    //       type: 'DELETE_USER',
    //       payload: id
    //     }),
    //   );
    return (dispatch)=>{
        
           dispatch({
               type:'DELETE_USER',
               payload:id
           })
  };
}
