// const initialState=[]
export const projectReducer = (state={ user: [] },action) => {
 switch (action.type) {
    case "FETCH_DATA":
       return  action.payload;
    case "ADD_USER"  :
        return{
        user:[...state.user,action.payload]
    }
    case "UPDATE_USER":
        const updatedUser=action.payload
        const updatedUsers=state.user.map(user=>{
            if(user.id=== updatedUser.id){
                return updatedUser;
            }
            return user;
        })
        return{
            users:updatedUsers
        }  
        case "DELETE_USER"  :
            return {
            //   users:  state.value.filter((user)=>user.id !== action.payload.id)
                ...state, 
                user: state.user.filter((users) => users.id !== action.payload.id) 
            }  
 
    default:
       return state;
 }
}
